// client/src/pages/TripPlanner.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { tripService } from '../services/tripService';
import { routeService } from '../services/routeService';
import RouteMap from '../components/RouteMap';
import LoadingSpinner from '../components/LoadingSpinner';
import { MapPin, Compass, Save, ArrowLeft } from 'lucide-react';
import toast from 'react-hot-toast';

const TripPlanner = () => {
  // טופס חדש: שמות מוצא/יעד, מצב מסלול, ימים, נק׳ ביניים לפי שם, רמז אזורי
  const [formData, setFormData] = useState({
    startName: '',
    endName: '',
    mode: 'hike',         // 'hike' | 'bike'
    days: 1,
    waypointsInput: '',   // פסיק-מופרד: "Modi'in, Latrun"
    regionHint: ''
  });

  const [loading, setLoading] = useState(false);
  const [routeData, setRouteData] = useState(null);   // מותאם ל-RouteMap: { points: [{lat,lng}, ...] }
  const [stats, setStats] = useState(null);           // { total_km, total_duration_h, daily: [...] }
  const [showSaveForm, setShowSaveForm] = useState(false);
  const [saveFormData, setSaveFormData] = useState({ name: '', description: '' });
  const [saving, setSaving] = useState(false);

  const navigate = useNavigate();

  // רשימת ערים נוחה לדוגמה — ניתן להחליף לשדות חופשיים (כבר יש גם קלט type="text")
  const suggestions = [
    'Tel Aviv, Israel',
    'Jerusalem, Israel',
    'Haifa, Israel',
    'Amsterdam, Netherlands',
    'Utrecht, Netherlands',
    'Paris, France',
    'London, UK',
    'New York, USA'
  ];

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSaveFormChange = (e) => {
    const { name, value } = e.target;
    setSaveFormData(prev => ({ ...prev, [name]: value }));
  };

  const generateRoute = async () => {
    if (!formData.startName || !formData.endName || !formData.mode) {
      toast.error('Please fill start, end and mode');
      return;
    }

    setLoading(true);
    setRouteData(null);
    setStats(null);
    setShowSaveForm(false);

    try {
      const waypoints = formData.waypointsInput
        ? formData.waypointsInput.split(',').map(s => s.trim()).filter(Boolean)
        : [];

      // קריאה ל־API החדש דרך tripService (יש לעדכן tripService.js עם planTripNew)
      const result = await tripService.planTripNew({
        startName: formData.startName,
        endName: formData.endName,
        mode: formData.mode,               // 'hike' | 'bike'
        days: Number(formData.days) || 1,
        waypoints,                         // שמות בלבד
        regionHint: formData.regionHint || ''
      });

      if (!result?.success) {
        throw new Error(result?.message || 'Route planning failed');
      }

      // RouteMap מצפה לשדה points [{lat,lng},...]
      const points = (result.points || []).map(p => ({ lat: p.lat, lng: p.lng }));
      setRouteData({ points });

      setStats({
        total_km: result.summary?.total_km ?? 0,
        total_duration_h: result.summary?.total_duration_h ?? 0,
        daily: result.daily || []
      });

      // שם ברירת מחדל למסלול לשמירה
      const autoName = `${formData.mode === 'hike' ? 'Hike' : 'Bike'}: ${formData.startName} → ${formData.endName}`;
      setSaveFormData(prev => ({ ...prev, name: autoName }));
    } catch (error) {
      console.error('Generate route error:', error);
      toast.error(error?.message || 'Failed to generate route');
    } finally {
      setLoading(false);
    }
  };

  const saveRoute = async () => {
    if (!routeData?.points?.length) {
      toast.error('No route data to save');
      return;
    }

    setSaving(true);
    try {
      // ניסיון לפענח עיר/מדינה מהשם (פשוט)
      const startParts = formData.startName.split(',').map(s => s.trim());
      const city = startParts[0] || 'Unknown';
      const country = startParts[1] || 'Unknown';

      // התאמה לסקימה הקיימת של routeService
      const routeToSave = {
        name: saveFormData.name,
        description: saveFormData.description,
        tripType: formData.mode === 'hike' ? 'hiking' : 'cycling',
        location: {
          country,
          city,
          coordinates: {
            lat: routeData.points[0].lat,
            lng: routeData.points[0].lng
          }
        },
        // אפשר לשמור גם את ה-GeoJSON המלא אם תרצי להרחיב את השרת
        routeData,   // כאן נשמר רק הפוליליין בנקודות, כפי ש-RouteMap צורך
        stats        // כולל summary + daily
      };

      await routeService.createRoute(routeToSave);
      toast.success('Route saved successfully!');
      navigate('/routes');
    } catch (error) {
      console.error('Save route error:', error);
      toast.error('Failed to save route');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="container py-6">
      <div className="flex items-center mb-6">
        <button className="btn btn-ghost mr-3" onClick={() => navigate(-1)}>
          <ArrowLeft className="h-4 w-4 mr-1" />
          Back
        </button>
        <h1 className="text-2xl font-bold">Trip Planner</h1>
      </div>

      <div className="grid lg:grid-cols-3 gap-6">
        {/* טופס פרטים */}
        <div className="lg:col-span-1">
          <div className="card">
            <div className="card-header">
              <h2 className="text-xl font-semibold text-gray-900">Trip Details</h2>
            </div>
            <div className="card-body space-y-6">

              {/* Start name */}
              <div>
                <label htmlFor="startName" className="block text-sm font-medium text-gray-700 mb-2">
                  <MapPin className="h-4 w-4 inline mr-1" />
                  Start (City, Country)
                </label>
                <input
                  id="startName"
                  name="startName"
                  type="text"
                  value={formData.startName}
                  onChange={handleFormChange}
                  list="suggestions"
                  placeholder="e.g., Tel Aviv, Israel"
                  className="input"
                />
              </div>

              {/* End name */}
              <div>
                <label htmlFor="endName" className="block text-sm font-medium text-gray-700 mb-2">
                  <MapPin className="h-4 w-4 inline mr-1" />
                  End (City, Country)
                </label>
                <input
                  id="endName"
                  name="endName"
                  type="text"
                  value={formData.endName}
                  onChange={handleFormChange}
                  list="suggestions"
                  placeholder="e.g., Jerusalem, Israel"
                  className="input"
                />
                <datalist id="suggestions">
                  {suggestions.map(s => <option key={s} value={s} />)}
                </datalist>
              </div>

              {/* Mode */}
              <div>
                <label htmlFor="mode" className="block text-sm font-medium text-gray-700 mb-2">
                  <Compass className="h-4 w-4 inline mr-1" />
                  Mode
                </label>
                <div className="space-y-2">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="mode"
                      value="hike"
                      checked={formData.mode === 'hike'}
                      onChange={handleFormChange}
                      className="mr-2"
                    />
                    <span className="text-sm">Hiking (trails/footpaths)</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="mode"
                      value="bike"
                      checked={formData.mode === 'bike'}
                      onChange={handleFormChange}
                      className="mr-2"
                    />
                    <span className="text-sm">Bike (roads/bike paths)</span>
                  </label>
                </div>
              </div>

              {/* Days */}
              <div>
                <label htmlFor="days" className="block text-sm font-medium text-gray-700 mb-2">
                  Days
                </label>
                <input
                  id="days"
                  name="days"
                  type="number"
                  min="1"
                  max="14"
                  value={formData.days}
                  onChange={handleFormChange}
                  className="input"
                />
              </div>

              {/* Waypoints by name */}
              <div>
                <label htmlFor="waypointsInput" className="block text-sm font-medium text-gray-700 mb-2">
                  Waypoints (optional, comma-separated)
                </label>
                <input
                  id="waypointsInput"
                  name="waypointsInput"
                  type="text"
                  value={formData.waypointsInput}
                  onChange={handleFormChange}
                  placeholder="e.g., Modi'in, Latrun"
                  className="input"
                />
              </div>

              {/* Region hint */}
              <div>
                <label htmlFor="regionHint" className="block text-sm font-medium text-gray-700 mb-2">
                  Region hint (optional)
                </label>
                <input
                  id="regionHint"
                  name="regionHint"
                  type="text"
                  value={formData.regionHint}
                  onChange={handleFormChange}
                  placeholder="e.g., Israel"
                  className="input"
                />
              </div>

              <button
                onClick={generateRoute}
                disabled={loading || !formData.startName || !formData.endName}
                className="w-full btn btn-primary"
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="spinner w-4 h-4 mr-2"></div>
                    Generating Route...
                  </div>
                ) : (
                  'Generate Route'
                )}
              </button>
            </div>
          </div>
        </div>

        {/* מפה + סטטיסטיקות + שמירה */}
        <div className="lg:col-span-2 space-y-6">
          {/* Map */}
          <div className="card">
            <div className="card-header">
              <h3 className="text-lg font-semibold text-gray-900">Route Map</h3>
            </div>
            <div className="card-body">
              {loading ? (
                <div className="flex justify-center py-10">
                  <LoadingSpinner />
                </div>
              ) : (
                <RouteMap
                  routeData={routeData}          // { points: [{lat,lng}, ...] }
                  center={[31.9, 34.8]}          // מרכז מפה ברירת מחדל (ישראל)
                  zoom={8}
                  showRoute
                  showMarkers
                  height="420px"
                />
              )}
            </div>
          </div>

          {/* Stats */}
          {stats && (
            <div className="card">
              <div className="card-header">
                <h3 className="text-lg font-semibold text-gray-900">Summary</h3>
              </div>
              <div className="card-body">
                <div className="grid grid-cols-3 gap-4">
                  <div className="text-center">
                    <div className="text-xl font-semibold">{stats.total_km} km</div>
                    <div className="text-xs text-gray-600">Total distance</div>
                  </div>
                  <div className="text-center">
                    <div className="text-xl font-semibold">{stats.total_duration_h} h</div>
                    <div className="text-xs text-gray-600">Estimated duration</div>
                  </div>
                  <div className="text-center">
                    <div className="text-xl font-semibold">{Number(formData.days) || 1}</div>
                    <div className="text-xs text-gray-600">Days</div>
                  </div>
                </div>

                {stats.daily && stats.daily.length > 0 && (
                  <>
                    <div className="mt-4 text-sm font-medium">Per-day breakdown</div>
                    <div className="space-y-2 mt-2">
                      {stats.daily.map(d => (
                        <div key={d.day} className="flex justify-between items-center p-2 bg-gray-50 rounded">
                          <span className="text-sm font-medium">Day {d.day}</span>
                          <span className="text-sm text-gray-600">
                            {d.km} km · ~{d.duration_h} h
                          </span>
                        </div>
                      ))}
                    </div>
                  </>
                )}
              </div>
            </div>
          )}

          {/* Save Route */}
          {routeData && !showSaveForm && (
            <div className="card">
              <div className="card-body">
                <button
                  onClick={() => setShowSaveForm(true)}
                  className="w-full btn btn-primary"
                >
                  <Save className="h-4 w-4 mr-2" />
                  Save this route
                </button>
              </div>
            </div>
          )}

          {routeData && showSaveForm && (
            <div className="card">
              <div className="card-header">
                <h3 className="text-lg font-semibold text-gray-900">Save Route</h3>
              </div>
              <div className="card-body space-y-4">
                <div>
                  <label htmlFor="routeName" className="block text-sm font-medium text-gray-700 mb-2">
                    Route Name
                  </label>
                  <input
                    id="routeName"
                    name="name"
                    type="text"
                    value={saveFormData.name || ''}
                    onChange={handleSaveFormChange}
                    className="input"
                    placeholder="Enter route name"
                  />
                </div>
                <div>
                  <label htmlFor="routeDescription" className="block text-sm font-medium text-gray-700 mb-2">
                    Description (Optional)
                  </label>
                  <textarea
                    id="routeDescription"
                    name="description"
                    value={saveFormData.description || ''}
                    onChange={handleSaveFormChange}
                    className="input"
                    rows="3"
                    placeholder="Add a description for this route"
                  />
                </div>

                <div className="flex gap-3">
                  <button
                    onClick={saveRoute}
                    disabled={saving}
                    className="btn btn-primary"
                  >
                    {saving ? 'Saving...' : (
                      <>
                        <Save className="h-4 w-4 mr-2" />
                        Save Route
                      </>
                    )}
                  </button>
                  <button
                    onClick={() => setShowSaveForm(false)}
                    className="btn btn-ghost"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          )}

        </div>
      </div>
    </div>
  );
};

export default TripPlanner;
