import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';

// ×™×¦×™×¨×ª ××™× ×¡×˜× ×¡ ×©×œ axios ×¢× ×”×’×“×¨×•×ª ×‘×¨×™×¨×ª ××—×“×œ
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// ×”×•×¡×¤×ª ×˜×•×§×Ÿ ×× ×§×™×™×
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export const tripService = {
  /**
   * ×ª×›× ×•×Ÿ ××¡×œ×•×œ ×—×“×©
   * ×©×•×œ×— ××™×§×•× ×¢× name, lat, lng ×•×¡×•×’ ××¡×œ×•×œ
   */
  async planTrip(location, tripType) {
    const payload = {
      location: {
        name: location.name,
        lat: Number(location.lat),
        lng: Number(location.lng),
      },
      tripType,
    };

    console.log('ğŸŸ¢ planTrip payload before send:', payload);

    const response = await api.post('/trip/plan', payload, {
      headers: { 'Content-Type': 'application/json' },
    });

    return response.data.data;
  },



  /**
   * ×©××™×¨×ª ××¡×œ×•×œ ×—×“×© ×‘××¡×“ ×”× ×ª×•× ×™×
   */
  async createRoute(route) {
    try {
      console.log('ğŸŸ¢ Saving route:', route.name);
      const response = await api.post('/trip/save', route, {
        headers: { 'Content-Type': 'application/json' },
      });
      return response.data;
    } catch (error) {
      console.error('âŒ createRoute error:', error.response?.data || error.message);
      throw error;
    }
  },

  /**
   * ×§×‘×œ×ª ×ª×—×–×™×ª ××–×’ ××•×•×™×¨
   */
  async getWeatherForecast(location, days = 3) {
    try {
      const response = await api.get(`/weather/${encodeURIComponent(location)}`, {
        params: { days },
      });
      return response.data.data;
    } catch (error) {
      console.error('âŒ getWeatherForecast error:', error.response?.data || error.message);
      throw error;
    }
  },

  /**
   * ×§×‘×œ×ª ××–×’ ××•×•×™×¨ × ×•×›×—×™
   */
  async getCurrentWeather(location) {
    try {
      const response = await api.get(`/weather/${encodeURIComponent(location)}/current`);
      return response.data.data;
    } catch (error) {
      console.error('âŒ getCurrentWeather error:', error.response?.data || error.message);
      throw error;
    }
  },
};
