import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const RouteMap = ({ 
  routeData, 
  center = [40.7128, -74.0060], 
  zoom = 10, 
  height = '400px',
  showMarkers = true,
  showRoute = true 
}) => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const overlayRef = useRef(null);

  useEffect(() => {
    if (!mapRef.current) return;

    // Ensure the container has proper dimensions
    const container = mapRef.current;
    if (container.offsetHeight === 0) {
      container.style.height = height;
    }

    // Initialize map with error handling
    try {
      const map = L.map(container, {
        center: center,
        zoom: zoom,
        zoomControl: true,
        scrollWheelZoom: true
      });

      // Add tile layer
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap contributors',
        maxZoom: 18
      }).addTo(map);

      mapInstanceRef.current = map;

      // Force a resize after initialization
      setTimeout(() => {
        if (mapInstanceRef.current) {
          mapInstanceRef.current.invalidateSize();
        }
      }, 100);

      return () => {
        if (mapInstanceRef.current) {
          mapInstanceRef.current.remove();
          mapInstanceRef.current = null;
        }
      };
    } catch (error) {
      console.error('Error initializing map:', error);
    }
  }, [center, zoom, height]);

  useEffect(() => {
    if (!mapInstanceRef.current) return;

    const map = mapInstanceRef.current;

    try {
      // ננקה רק שכבות ציור קודמות שלנו (geojson/polylines/markers) – נשמור אותן בקבוצה אחת
      if (overlayRef.current) {
        map.removeLayer(overlayRef.current);
        overlayRef.current = null;
      }
      overlayRef.current = L.layerGroup().addTo(map);

      // אם אין Route – נשאיר סמן במרכז (כמו שהיה)
      if (!routeData) {
        if (showMarkers) {
          L.marker(center).addTo(overlayRef.current)
            .bindPopup('Location')
            .openPopup();
        }
        return;
      }

      // 1) אם יש geometry מהשרת (מומלץ) – מציירים ישירות GeoJSON
      if (showRoute && routeData.geometry) {
        const geoLayer = L.geoJSON(routeData.geometry, {
          style: { weight: 4, opacity: 0.9 }
        }).addTo(overlayRef.current);

        // סמני התחלה/סיום מתוך ה־geometry (LineString)
        if (showMarkers && routeData.geometry.type === 'LineString' && Array.isArray(routeData.geometry.coordinates)) {
          const coords = routeData.geometry.coordinates; // [ [lon,lat], ... ]
          if (coords.length > 0) {
            const start = [coords[0][1], coords[0][0]]; // [lat, lng]
            L.marker(start).addTo(overlayRef.current).bindPopup('Start').openPopup();
          }
          if (coords.length > 1) {
            const end = [coords[coords.length - 1][1], coords[coords.length - 1][0]];
            L.marker(end).addTo(overlayRef.current).bindPopup('End');
          }
        }

        // התאמת תצוגה למסלול
        map.fitBounds(geoLayer.getBounds(), { padding: [20, 20] });
        return; // סיימנו במקרה של geometry
      }

      // 2) אחרת – נפעל כמו קודם עם points (נשאר כתמיכה לאחור)
      if (showRoute && routeData.points && routeData.points.length > 0) {
        // קיבוץ נקודות לפי יום
        const dailyRoutes = {};
        routeData.points.forEach(point => {
          if (!dailyRoutes[point.day]) dailyRoutes[point.day] = [];
          dailyRoutes[point.day].push([point.lat, point.lng]);
        });

        const colors = ['#3B82F6', '#EF4444', '#10B981', '#F59E0B', '#8B5CF6'];
        let allBounds = null;

        Object.keys(dailyRoutes).forEach((day, index) => {
          const points = dailyRoutes[day];
          const color = colors[index % colors.length];

          const routeLine = L.polyline(points, {
            color,
            weight: 4,
            opacity: 0.8
          }).addTo(overlayRef.current);

          if (showMarkers && points.length > 0) {
            L.marker(points[0]).addTo(overlayRef.current).bindPopup(`Day ${day} - Start`).openPopup();
            if (points.length > 1) {
              L.marker(points[points.length - 1]).addTo(overlayRef.current).bindPopup(`Day ${day} - End`);
            }
          }

          // נאסוף bounds של כל הימים ואז נתאים פעם אחת
          const b = routeLine.getBounds();
          allBounds = allBounds ? allBounds.extend(b) : b;
        });

        if (allBounds) {
          map.fitBounds(allBounds, { padding: [20, 20] });
        }
        return;
      }

      // אם אין routeData.points או showRoute=false – רק סמן במרכז
      if (showMarkers) {
        L.marker(center).addTo(overlayRef.current).bindPopup('Location').openPopup();
      }
    } catch (error) {
      console.error('Error updating map:', error);
    }
  }, [routeData, showMarkers, showRoute, center]);


  return (
    <div 
      ref={mapRef} 
      style={{ height, width: '100%', minHeight: '200px' }}
      className="rounded-lg overflow-hidden"
    />
  );
};

export default RouteMap;
