import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';

// יצירת אינסטנס של axios עם הגדרות ברירת מחדל
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// הוספת טוקן אם קיים
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export const tripService = {
  /**
   * תכנון מסלול חדש
   * שולח מיקום עם name, lat, lng וסוג מסלול
   */
  async planTrip(location, tripType) {
    // וידוא שאנחנו שולחים את המידע כמו שהשרת מצפה
    const payload = {
      location: {
        name: location.name,
        lat: location.lat,
        lng: location.lng,
      },
      tripType,
    };

    console.log('🟢 planTrip payload before send:', payload);

    // שליחה כ־JSON אמיתי
    const response = await api.post('/trip/plan', payload, {
      headers: { 'Content-Type': 'application/json' },
    });

    return response.data.data;
  },



  /**
   * שמירת מסלול חדש במסד הנתונים
   */
  async createRoute(route) {
    try {
      console.log('🟢 Saving route:', route.name);
      const response = await api.post('/trip/save', route, {
        headers: { 'Content-Type': 'application/json' },
      });
      return response.data;
    } catch (error) {
      console.error('❌ createRoute error:', error.response?.data || error.message);
      throw error;
    }
  },

  /**
   * קבלת תחזית מזג אוויר
   */
  async getWeatherForecast(location, days = 3) {
    try {
      const response = await api.get(`/weather/${encodeURIComponent(location)}`, {
        params: { days },
      });
      return response.data.data;
    } catch (error) {
      console.error('❌ getWeatherForecast error:', error.response?.data || error.message);
      throw error;
    }
  },

  /**
   * קבלת מזג אוויר נוכחי
   */
  async getCurrentWeather(location) {
    try {
      const response = await api.get(`/weather/${encodeURIComponent(location)}/current`);
      return response.data.data;
    } catch (error) {
      console.error('❌ getCurrentWeather error:', error.response?.data || error.message);
      throw error;
    }
  },
};
