import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const RouteMap = ({ 
  routeData, 
  center = [40.7128, -74.0060], 
  zoom = 10, 
  height = '400px',
  showMarkers = true,
  showRoute = true 
}) => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);

  useEffect(() => {
    if (!mapRef.current) return;

    // Ensure the container has proper dimensions
    const container = mapRef.current;
    if (container.offsetHeight === 0) {
      container.style.height = height;
    }

    // Add a small delay to prevent conflicts with other UI interactions
    const initMap = () => {
      // Initialize map with error handling
      try {
        const map = L.map(container, {
          center: center, // Use provided center
          zoom: zoom,
          zoomControl: true,
          scrollWheelZoom: true
        });

        // Add tile layer
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '© OpenStreetMap contributors',
          maxZoom: 18
        }).addTo(map);

        mapInstanceRef.current = map;

        // Force a resize after initialization
        setTimeout(() => {
          if (mapInstanceRef.current) {
            mapInstanceRef.current.invalidateSize();
          }
        }, 100);

        return () => {
          if (mapInstanceRef.current) {
            mapInstanceRef.current.remove();
            mapInstanceRef.current = null;
          }
        };
      } catch (error) {
        console.error('Error initializing map:', error);
      }
    };

    // Small delay to prevent UI conflicts
    const timeoutId = setTimeout(initMap, 50);

    return () => {
      clearTimeout(timeoutId);
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
    };
  }, [center, zoom, height, routeData]); // Removed mapCenter from dependencies

  useEffect(() => {
    if (!mapInstanceRef.current) return;

    const map = mapInstanceRef.current;

    try {
      // Clear existing layers
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker || layer instanceof L.Polyline) {
          map.removeLayer(layer);
        }
      });

      // Draw route if routeData is provided
      if (showRoute && routeData && routeData.points && routeData.points.length > 0) {
        // Group points by day
        const dailyRoutes = {};
        routeData.points.forEach(point => {
          if (!dailyRoutes[point.day]) {
            dailyRoutes[point.day] = [];
          }
          dailyRoutes[point.day].push([point.lat, point.lng]);
        });

        // Draw route for each day with different colors
        const colors = ['#3B82F6', '#EF4444', '#10B981', '#F59E0B', '#8B5CF6'];

        Object.keys(dailyRoutes).forEach((day, index) => {
          const points = dailyRoutes[day];
          const color = colors[index % colors.length];

          // Draw route line
          const routeLine = L.polyline(points, {
            color: color,
            weight: 5,
            opacity: 0.8
          }).addTo(map);

          // Add start and end markers
          if (showMarkers && points.length > 0) {
            // Start marker
            L.marker(points[0]).addTo(map)
              .bindPopup(`Day ${day} - Start`)
              .openPopup();

            // End marker
            if (points.length > 1) {
              L.marker(points[points.length - 1]).addTo(map)
                .bindPopup(`Day ${day} - End`);
            }
          }

          // Fit map to show all routes
          map.fitBounds(routeLine.getBounds(), { padding: [20, 20] });
        });
      }

      // If no route data, just show a marker at center
      if (!routeData && showMarkers) {
        L.marker(center).addTo(map) // Use center here
          .bindPopup('Location')
          .openPopup();
      }
    } catch (error) {
      console.error('Error updating map:', error);
    }

  }, [routeData, showMarkers, showRoute, center]); // Removed mapCenter from dependencies

  return (
    <div 
      ref={mapRef} 
      style={{ height, width: '100%', minHeight: '200px' }}
      className="map-container rounded-lg overflow-hidden"
    />
  );
};

export default RouteMap;
